import { Component } from '@angular/core';
import { ProductsDataService } from '../service/data/products-data.service';
import { Product } from '../product/product.component';
import { Router } from '@angular/router';

export class Category{

  constructor(public category:String){}

}

@Component({
  selector: 'app-category',
  templateUrl: './category.component.html',
  styleUrls: ['./category.component.css']
})

export class CategoryComponent {

  categories: Category[] = []

  products: Product[] = []

  constructor(private productsService: ProductsDataService,
    private router: Router){}

  ngOnInit(){  
  this.productsService.retriveCategories().subscribe(
    response => {
      console.log(response)
      this.categories = response
    }
  )
  }

  getProductsByCategory(category: Category){
    this.router.navigate(['/products/category', category])
    this.productsService.retriveProductsByCategory(String(category)).subscribe(
      response => {
        console.log(response)
        this.products = response
      }
    )
  }

}



<h1>Category</h1>

<div class="container">
    <table class="table">
        <thead>
            <tr>
            </tr>
        </thead>
        <tbody>
            <tr *ngFor="let category of categories">
                <td><span style="cursor: pointer; text-decoration: underline;" (click)="getProductsByCategory(category)">{{category}}</span></td>
                <!--<td><a href="/products/category/{{category}}" (click)=getProductsByCategory(category)>{{category}}</a></td>-->
            </tr>
        </tbody>
    </table>
</div>

<div class="container" *ngIf="products">
    <table class="table">
        <thead>
            <tr>
                <th>Product ID</th>
                <th>Title</th>
                <th>Price</th>
                <th>Description</th>
                <th>Category</th>
                <th>Image</th>
            </tr>
        </thead>
        <tbody>
            <tr *ngFor="let product of products">
                <td>{{product.id}}</td>
                <td>{{product.title}}</td>
                <td>{{product.price}}</td>
                <td>{{product.description}}</td>
                <td>{{product.category}}</td>
                <td><img [src] = "product.image" alt = "xyz"> </td>
            </tr>
        </tbody>
    </table>
</div>
